mapper.insert:

  SELECT calendar_seq.NEXTVAL FROM dual

INSERT INTO calendar (
  calendarno, usersno, title, content, labeldate, label,
  seqno, regdate, summary, emotion
) VALUES (
  #{calendarno}, #{usersno}, #{title}, #{content}, #{labeldate}, #{label},
  #{seqno}, sysdate, #{summary}, #{emotion}
)

mapper.update:
UPDATE calendar
SET cnt = cnt + 1
WHERE calendarno = #{calendarno}

mapper.select:
SELECT emotion, COUNT(*) AS cnt
FROM calendar
WHERE SUBSTR(labeldate, 1, 7) = #{labeldate}
  AND usersno = #{usersno}
GROUP BY emotion

mapper.update:
UPDATE calendar
SET summary = #{summary},
    emotion = #{emotion}
WHERE calendarno = #{calendarno}

mapper.select:
SELECT calendarno, labeldate, label, title, content, cnt, seqno, regdate, usersno, summary, emotion FROM ( SELECT calendarno, labeldate, label, title, content, cnt, seqno, regdate, usersno, summary, emotion, ROWNUM AS r FROM ( SELECT calendarno, labeldate, label, title, content, cnt, seqno, regdate, usersno, summary, emotion FROM calendar WHERE 1=1
AND ( UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' )
AND usersno = #{usersno}
ORDER BY labeldate DESC, seqno ASC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT COUNT(*) AS cnt FROM calendar WHERE 1=1
AND ( UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' )
AND usersno = #{usersno}

SELECT calendarno, labeldate, label, title, content, cnt, seqno, regdate,
       usersno, summary, emotion, calendar_type, emoji AS emoji
FROM calendar
WHERE labeldate = #{labeldate}
  AND usersno = #{usersno, jdbcType=INTEGER}
