mapper.insert:
INSERT INTO reply (
  replyno, content, rdate, usersno, postno, parentno
) VALUES (
  reply_seq.NEXTVAL, #{content}, SYSDATE, #{usersno}, #{postno}, #{parentno}
)

mapper.update:
UPDATE reply
SET content = #{content}
WHERE replyno = #{replyno}

mapper.delete:
DELETE FROM reply
WHERE replyno = #{replyno}
   OR parentno = #{replyno}
