mapper.select:
SELECT speciesno, grp, sname, cnt, seqno, visible, sdate
FROM species
WHERE speciesno=#{speciesno}

mapper.update:
UPDATE species 
SET grp=#{grp}, sname=#{sname}, cnt=#{cnt}, seqno=#{seqno}, visible=#{visible}, sdate=SYSDATE 
WHERE speciesno=#{speciesno}

mapper.select:
SELECT speciesno, grp, sname, cnt, seqno, visible, sdate, r FROM ( SELECT speciesno, grp, sname, cnt, seqno, visible, sdate, rownum as r FROM ( SELECT speciesno, grp, sname, cnt, seqno, visible, sdate FROM species
WHERE (UPPER(grp) LIKE '%' || UPPER(#{word}) || '%') OR (UPPER(sname) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY seqno ASC ) ) WHERE r >= #{start_num} AND r <= #{end_num}
